query:
query search(
  $query: String!
  $currentPage: Int
  $pageSize: Int
  $sort: String
) {
  search(
    query: $query
    currentPage: $currentPage
    pageSize: $pageSize
    sort: $sort
  ) {
    type
    breadcrumbs {
      facetCode
      facetName
      facetValueCode
      facetValueName
      removeQuery {
        query {
          value
        }
        url
      }
      truncateQuery {
        query {
          value
        }
        url
      }
    }
    categoryCode
    categoryHierarchy {
      code
      count
      selected
      childSelected
      children
      depth
      leaf
      parentSelected
    }
    categoryName
    currentQuery {
      query {
        value
      }
      url
    }
    facets {
      category
      multiSelect
      name
      priority
      visible
      code
      topValues {
        count
        name
        selected
        query {
          query {
            value
          }
          url
        }
      }
      values {
        count
        name
        selected
        query {
          query {
            value
          }
          url
        }
      }
    }
    freeTextSearch
    keywordRedirectUrl
    lscoBreadcrumbs {
      name
      url
      linkClass
    }
    pagination {
      currentPage
      pageSize
      sort
      totalPages
      totalResults
    }
    products {
      code
      name
      url
      swatchUrl
      swatchAltText
      galleryList {
        galleryImage {
          altText
          format
          galleryIndex
          imageType
          url
        }
      }
      purchasable
      price {
        code
        currencyIso
        formattedValue
        hardPrice
        hardPriceFormattedValue
        regularPrice
        regularPriceFormattedValue
        softPrice
        softPriceFormattedValue
        value
      }
      baseProduct
      soldIndividually
      colorName
      colorGroup
      comingSoon
      averageOverallRatings
      noOfRatings
      soldOutForever
      sustainability
      findInStoreEligible
      customizable
      flxCustomization
      variantOptions {
        code
        comingSoon
        customizable
        findInStoreEligible
        flxCustomization
        swatchUrl
        swatchAltText
        galleryList {
          galleryImage {
            altText
            format
            galleryIndex
            imageType
            url
          }
        }
        merchantBadge
        promotionalBadge
        sustainability
        name
        priceData {
          hardPrice
          hardPriceFormattedValue
          regularPrice
          regularPriceFormattedValue
          softPrice
          softPriceFormattedValue
          value
          currencyIso
        }
        soldIndividually
        soldOutForever
        url
      }
      lscoBreadcrumbs {
        categoryCode
        name
        url
      }
      merchantBadge
      promotionalBadge
      errors {
        component
        name
        time_thrown
        message
      }
    }
    seoMetaData {
      canonicalUrl
      metaDescription
      metaH1
      metaTitle
    }
    sorts {
      code
      name
      selected
    }
    spellingSuggestion {
      query
      suggestion
    }
  }
}
